#define rot(a)       mat2(cos(a+vec4(0,11,33,0)))         // rotation                  

void mainImage_(out vec4 O, vec2 U)
{
    float t=9.; 
    vec3  R = iResolution,
          D = normalize(vec3(U+U, -3.5*R.y) - R),         // ray direction
          p = 60./R, q,a,                                 // marching point along ray 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(15,5,0)/1e2*cos(iTime+vec3(0,11,0)); // auto thumbnail

    for (O=vec4(1); O.x > 0. && t > .01; O-=.01) {
        q = p,
        q.yz *= rot(-6.*M.y),                             // rotations
        q.xz *= rot(-6.*M.x);
        a = abs(q);
        if ( q.z < -1. ) { O-=O;  break; }
        if ( a.z<2. && max(a.x,a.y) > 15. ) continue;
        for(int i=0; i<4; i++)
          a = q,
          a.xy = fract(a.xy) - vec2(i%2,i/2),
          a.xz *= rot(iTime-.5*length(q) ),
          a = abs(a),
          t = min(t, max(max(a.x-.4,a.y-.45),a.z-.02) ); 

       p += .5*t*D;                                       // step forward = dist to obj          
   }
  O*=O*O*2.;
}


void mainImage(out vec4 O, vec2 U)                        // --- smart oversampling
{
    mainImage_(O,U);
    if ( fwidth(O.x) > .1 ) {
        vec4 O_; 
        for(int i=0; i<9; i+=i==3?2:1) {
            mainImage_(O_,U+vec2(i%3-1,i/3-1)/3. );
            O += O_;
        }
        O /= 9.;  // O.r++;  // uncomment to see oversampled pixels
    }
}
