#define PI     3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = vec3(0,0,0);

    float temp_line = 0.0;

    float bun = 256.0;
    float haba = 2.0/bun;;
    vec2 centre = vec2(0.5);
    float speed = 0.1;
    float hiritu = iResolution.x/iResolution.y;
    centre += vec2(cos(iTime * speed),sin(iTime * speed)*hiritu) * 0.05;

    for( float i = 0.0;
    i < mod(iTime * bun * 0.25,bun);
    i++){
    vec2 line = vec2(1,1);
    line = vec2(cos(PI/bun * i),sin(PI/bun * i));
    //line = normalize(line);

    float temp = abs(dot(uv - centre,line));
    temp = smoothstep(max(0.0001,haba * distance(uv,centre)),0.0,temp);
    temp_line += temp;
    }
    float move_line = temp_line;

    for( float i = 0.0;
    i < bun;
    i++){
    vec2 line = vec2(1,1);
    line = vec2(cos(PI/bun * i),sin(PI/bun * i));
    //line = normalize(line);

    float temp = abs(dot(uv - vec2(0.5),line));
    temp = smoothstep(max(0.0001,haba * distance(uv,centre)),0.0,temp);
    temp_line += temp;
    }

    //temp_line *= move_line;
    col = vec3(move_line,temp_line * 0.5,move_line * temp_line);

    // Output to screen
    fragColor = vec4(col,1.0);
}
