void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float v = texture(iChannel0, uv).y / 0.15; 
    if(iFrame<10){
    fragColor = vec4(0.);
    return;
    }
    fragColor = v *sin(v*3.)*vec4(0.400,0.882,0.455,0.0);
}

vec2 lp(vec2 uv)
{
    vec2 g = vec2(1.,0.);    
    vec3 k = vec3(.05,.2,-1.);                          
    vec2 lp = vec2(0.);
    lp += texture(last,uv-g.xx/R.xy).xy * k.x;
    lp += texture(last,uv-g.yx/R.xy).xy * k.y;
    lp += texture(last,uv+(g.xy-g.yx)/R.xy).xy * k.x;
    lp += texture(last,uv-g.xy/R.xy).xy * k.y;
    lp += texture(last,uv).xy * k.z;
    lp += texture(last,uv+g.xy/R.xy).xy * k.y;
    lp += texture(last,uv+(-g.xy+g.yx)/R.xy).xy * k.x;
    lp += texture(last,uv+g.yx/R.xy).xy * k.y;
    lp += texture(last,uv+g.xx/R.xy).xy * k.x;
    return lp;
}

void mainImage( out vec4 O,in vec2 U )
{ 
    vec2 uv = U/R.xy;

    if(iFrame < 1){
    vec4 t = vec4(1.,0.,0.,1.);
     vec2 vw = (2.*U-R.xy)/R.x;
    t.y = .2-length(vw);
    O = t;
    return;
    }

    vec2 t = texture(last,uv).xy;
    vec2 lp = lp(uv);
    float r = (t.x*t.y*t.y);
    float f = mix(cf.x,cf.y,abs(uv.y-0.5));
    float k = mix(ck.x,ck.y ,abs(uv.x-0.5));
    t.x += DA*lp.x-r+f*(1.-t.x);
    t.y += DB*lp.y+r-(k+f)*t.y;
    O = vec4(t,0.,1.);
}

