vec4 plasma(vec2 fragCoord) {
    float r, g ,b;

    fragCoord.y = -fragCoord.y;

    float verticalBarsScale = 1.5, horizontalBarsScale = 1.5, diagonalBarsScale = 1.5, circleBarsScale = 1.5;
    float verticalBarsIntensity = 1.1, horizontalBarsIntensity = 1., diagonalBarsIntensity = 1.1, circleBarsIntensity = .5;


    r = 
    (
        //Vetical bars
        .5 + (verticalBarsIntensity * sin(-iTime+fragCoord.x / (32.0*verticalBarsScale)))

      //Horizontal bars
      + .5 + (horizontalBarsIntensity * sin(fragCoord.y / (40.0*horizontalBarsScale)))

      //Diagonal bars
      + .5 + (diagonalBarsIntensity * sin((fragCoord.x + fragCoord.y) / (32.0*diagonalBarsScale)))

      //Circle
      + .5 + (circleBarsIntensity * sin(sqrt((fragCoord.x * fragCoord.x + fragCoord.y * fragCoord.y)) / (80.0*circleBarsScale)))
    ) / 4.0;

    g = 
    (
        //Vetical bars
        .5 + (verticalBarsIntensity * sin(-2.0*iTime+fragCoord.x / (48.0*verticalBarsScale)))

      //Horizontal bars
      + .5 + (horizontalBarsIntensity * sin(fragCoord.y / (40.0*horizontalBarsScale)))

      //Diagonal bars
      + .5 + (diagonalBarsIntensity * sin((fragCoord.x + fragCoord.y) / (32.0*diagonalBarsScale)))

      //Circle
      + .5 + (circleBarsIntensity * sin(sqrt((fragCoord.x * fragCoord.x + fragCoord.y * fragCoord.y)) / (80.0*circleBarsScale)))
    ) / 4.0;

    b = 
    (
        //Vetical bars
        .5 + (verticalBarsIntensity * sin(-3.0*iTime+fragCoord.x / (24.0*verticalBarsScale)))

     //Horizontal bars
      + .5 + (horizontalBarsIntensity * sin(fragCoord.y / (40.0*horizontalBarsScale)))

      //Diagonal bars
      + .5 + (diagonalBarsIntensity * sin((fragCoord.x + fragCoord.y) / (32.0*diagonalBarsScale)))

      //Circle
      + .5 + (circleBarsIntensity * sin(sqrt((fragCoord.x * fragCoord.x + .5*fragCoord.y * fragCoord.y)) / (80.0*circleBarsScale)))
    ) / 4.0;

    return vec4(r,g,b, 1.0) + vec4(.2, .12, .0, 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = plasma(fragCoord);
}
