#define PHI 0.61803398875

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1,1,1);

    float maxi = mod(float(iTime*5.),400.);
    maxi *= uv.y;

    for(float i=0.; i<maxi; i++)
    {
        col*= smoothstep(.001,.002,abs(uv.x-.5));
        uv.x=fract(uv.x+PHI);
    }

    fragColor = vec4(col,1.0);
}
