float random(float t)
{
    return fract(sin(t * 8239.12));
}

float random(vec2 uv, float t)
{
    return fract(sin(dot(uv, vec2(123.23, 34.21))) * 53792.2131 + t);
}

float box(vec2 uv, vec2 pos, vec2 m)
{
    return (step(pos.y-m.y,uv.y)-step(pos.y+m.y,uv.y))*
           (step(pos.x-m.x,uv.x)-step(pos.x+m.x,uv.x));
}

float lineSpeak(vec2 uv, vec2 st, vec2 size, float t, float seed)
{
    int num = int(t * 10.);

    int cap = int(random(size, 0.) * 25.);
    int count = 0;
    int down = 0;
    float r = 0.;

    for(int i = 0; i < num; i++)
    {
        r = box(uv, st + vec2(0.04 * float(count),float(down) * -0.1), 
        size*(vec2(random(seed*float(i)*1.)+ sin(t*3947.2*float(i))*0.2,random(seed*float(i))*3.)));
        if (r > 0.5)
        {
            break;
        }
        else if (count >= cap)
        {
            down++;
            count=0;
            cap = int(random(size + vec2(float(i)),float(i)*seed)*25.);
        }
        else
        {
            count++;
        }
    }

    return r;
}

