#define PI 3.14159265

float lineseg(vec2 u, vec2 s, vec2 e)
{
    //shift coords to 0,0
    vec2 l1 = u-s;
    vec2 e2 = e-s;
    //formula to find nearest projection of u on se
    vec2 l2 = e2*(clamp( dot(l1,e2) /(dot(e2,e2)), 0., 1. ) );

    return 1.-smoothstep(.0,.02,length(l1-l2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 10.4*(fragCoord-.5*iResolution.xy)/iResolution.x;

    vec3 col = vec3(0);

    for(float i = 0.; i < 250.; i++)
    {
        vec4 p = texelFetch(iChannel0, ivec2(i,0), 0);

        float x = 1.5*sin(p.x);
        float y = -1.5*cos(p.x);
        float x2 = x + 1.5*sin(p.y);
        float y2 = y - 1.5*cos(p.y);

        vec3 c = 0.5 + .5 * sin(i*PI/125.0+vec3(-PI,0,PI)/1.5);

        c *= .2;

        col += c*lineseg(uv, vec2(0), vec2(x,y));
        col += c*lineseg(uv, vec2(x,y), vec2(x2,y2));
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

//need to store for each pendulum:
//theta1, theta2, v1, v2
//x       y       z   w

//https://www.myphysicslab.com/pendulum/double-pendulum-en.html

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 p = texelFetch(iChannel0, ivec2(fragCoord),0);

    //reset every 15 secs
    if(iMouse.z > 0.5 || iFrame <= 1)
    {
        float x1 = fragCoord.x/iResolution.x;

        p = vec4(2.14+fract(x1*.1)+iTime,1,0,0);
    }
    else if(fragCoord.x < 250. && fragCoord.y <= 1.)
    {

        //update position based on velocities
        p.x += p.z;
        p.y += p.w;

        //update velocities
        float div = (2. + 1. - cos(2.*p.x-2.*p.y));

        p.z += 0.00005*((-G*(2.+1.)*sin(p.x)-G*sin(p.x-2.*p.y)-2.*sin(p.x-p.y)*(p.w*p.w+p.z*cos(p.x-p.y)))/div);
        p.w += 0.00005*((2.*sin(p.x-p.y)*(p.z*p.z*(2.)+G*(1.+1.)*cos(p.x)+p.w*p.w*cos(p.x-p.y)))/div);

        //friction...
        p.zw /= 1.0001;

    }

    fragColor = p;
}
