void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	 vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
	//vec2 uv=(2.0*gl_FragCoord.xy-resolution)/max(resolution.x,resolution.y);

	float t = iTime * .5;

	float f = 0.;

	 vec3 rCol = vec3(.3);

	  float moveFactor = .1;
 	 vec2 vuv = vec2(0.);
	vuv = uv + vec2(moveFactor, 0.);

	f = step(fract(length(vuv)*2.   - iTime), .5);

	f *= step(fract(uv.y * 10. + t), .5);

	 f += step(fract(vuv.y * 7. - abs(vuv.x * 7.) + t), .5);

        rCol = mix(rCol, vec3(1.), f);


	fragColor = vec4(rCol,1.0);

}
