void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mx = max(iResolution.x,iResolution.y);
    vec2 z0 = (fragCoord-iResolution.xy/2.)/mx*vec2(1.,-1.)*5.;
    float t = iTime;

    vec2 u = vec2(-cos(t), sin(t*1.1));
    vec2 z = z0;

    for(int i=0;i<4;i++){
        z=u+cMult(z,z);
    }

    vec3 col = (0.5 + 0.5*cos(atan(z.y,z.x)+(vec3(0,2,4)/3.)*TAU))
              /pow(1.+length(z),.5)*(sin(length(z)+1./length(z))*.5+.5);

    fragColor = vec4(col,1.0);
}

const float PI = 3.1415926;
const float TAU = PI*2.;

vec2 cMult(vec2 u, vec2 v){
    return vec2(u.x*v.x-u.y*v.y,
                u.x*v.y+u.y*v.x);
}
