void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 6.28;

    float sine = ((sin(uv.x + iTime) + 1.0) / 2.0) - (uv.y/6.28 - 0.499);
    float roundsine = round(sine);
    float invertroundsine = 1.0 - round(sine + .002);
    float sumsine = roundsine + invertroundsine;
    vec3 col = (vec3(sumsine, sumsine, sumsine));

    fragColor = vec4(col,1.0);
}
