// https://iquilezles.org/articles/fbm
float fbm(vec2 x, float H)
{
        float G = exp2(-H);
        float f = 1.0;
        float a = 1.0;
        float t = 0.0;

        for (int i=0; i < 3; i++) {
                t += a * texture(iChannel0, f * x).r;
                f *= 2.0;
                a *= G;
        }

        return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = .03 * (2. * fragCoord - iResolution.xy) / iResolution.y;

    float x = fbm(uv + .01 * normalize(vec2(-1.0, 1.2)) * iTime, 2.);
    float y = fbm(uv + .01 * normalize(vec2(1.5, 1.9)) * iTime, 2.);

    vec3 col = vec3(1.) * 2. * smoothstep(1., -1., 10. * abs(x - y + 2. * mod(.75 * iTime, 1.) - 1.));
    fragColor = vec4(col,1.0);
}
