void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvO = vec2(uv.x,uv.y - iTime/2.7);
    vec2 uvSlow = vec2(uv.x,uv.y - iTime/4.2);
    vec2 uvSlower = vec2(uv.x,uv.y - iTime/6.3);
    vec2 uvDiag1 = vec2(uv.x - iTime/12.1,uv.y - iTime/6.1);
    vec2 uvDiag2 = vec2(uv.x + iTime/14.0,uv.y - iTime/6.0);

    float scaleFactor = 1.0;
    vec2 uvScaled = vec2(uv.x/scaleFactor,uv.y/scaleFactor);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 texStop =   texture(iChannel1,uv);
    vec4 texMove =   texture(iChannel1,uvO);
    vec4 texSlow =   texture(iChannel1,uvSlow);
    vec4 texSlower = texture(iChannel1,uvSlower);
    vec4 texDiag1 =  texture(iChannel0,uvDiag1);
    vec4 texDiag2 =  texture(iChannel0,uvDiag2);

    float distCenter = sqrt(abs(0.5-uv.x)*abs(0.5-uv.x)+abs(0.5-uv.y)*abs(0.5-uv.y));

    float verticalFalloff = 0.0;
    float circleFalloff = 2.0;

    float colorMagnitude =
    //vert falloff calc
    (1.0 - ((verticalFalloff) * uv.y)) *
    //circle falloff calc
    (1.0 - (circleFalloff * distCenter)) *
    (
    //texStop.x *
    texMove.x *   0.2 +
    texSlow.x *   0.2 +
    texSlower.x * 0.2 +
    texDiag1.x * 0.05  +
    texDiag2.x * 0.05
    )
    ;

    vec3 color = vec3(
    colorMagnitude/4.0
    ,
    colorMagnitude/1.2
    ,
    colorMagnitude
    );


    // Output to screen
    fragColor = vec4(color,1.0);
}
