precision highp float;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    float a = 0.01;
    float x = 0.9;
    float y = 0.9;
    vec3 color0 = vec3(a/abs(uv.y - y) + a/abs(uv.x - x));
    vec3 color1 = vec3(a/abs(uv.y + y) + a/abs(uv.x + x));
    fragColor = vec4(color0, 1.) + vec4(color1, 1.);
}
