// line_gap determines size of black lines
const float line_gap = 1.0; // px
// line_size is visible height
const float line_size = 4.0; // px

// parameters of ellipse
const float a = .6, b = .8;

// function for laggy, noisy shift of image
float noise(float x) {
    return (sin(3.0*x)
        + cos(8.0*x)
        - cos(3.0 + 14.0 * x)
        - sin(x * x / 2.0)
    ) / 4.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0 * fragCoord/ iResolution.xy - 1.0;

    float len = 1.0 - (pow(abs(uv.x), 9.0) / a + pow(abs(uv.y), 3.0) / b);

    // uncomment for visible edges
    // len = step(0.0, len);

    if (len > 0.0) {
        vec4 intensity = vec4(len, len, len, 1.0);

        vec2 skew = vec2(0.0);
        skew.x = smoothstep(0.7, 1.0, noise(iTime)) / 10.0;



        float modulo = line_gap + line_size;
        if (mod(fragCoord.y + modulo / 2.0, modulo) >= line_gap) {
            fragColor = texture(iChannel0, (uv + vec2(1.0))/2.0 + skew) * intensity;
        } else {
            fragColor = vec4(vec3(0.0), 1.0);
        }
    } 

}
