// https://www.shadertoy.com/view/7dByRz
// Based on https://www.shadertoy.com/view/sdscDs insulate by JT
// Apply the "insulate" operator to TLC's 2d tree sdf to expand it into a 3d sdf.

#define EPSILON 0.001
#define DIST_MAX 50.0

// https://iquilezles.org/articles/distfunctions2d
float halfspace(vec3 p, float d)
{
    return p.z - d;
}

// https://www.shadertoy.com/view/sl2SRt 2d tree branching SDF by TLC123
float PI = acos(-1.0);
float TAU = acos(-1.0) * 2.0;

float smin(float a, float b,float k )
{
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0) ;
    return mix(b, a, h ) - k * h * (1.0 - h);
}

float smax(float a, float b, float k)
{
    return -smin(-a,-b, k);
}

float TLC123_tree2d(vec2 uv, int branches)
{
    // to polar
    vec2 p = vec2((atan(uv.x, uv.y) / TAU) * float(branches), length(uv));

    float Y, y, X;
    Y = exp2(floor(p.y));
    y = 0.5 + (pow(fract(p.y), 0.85)) * 0.5 + 0.5;
    X = abs(p.x * Y);
    p.x = abs(fract(X - y * .5) - 0.5) * 2.0;
    p.x = max(p.x, abs(fract(X + y * 0.5) - 0.5) * 2.0);

    float branchwidth = 0.1 / (0.25 + p.y);
    float outerlimit = p.y - 4.6;
    float scaleAdjusted = 1.5 * (((1.0 - p.x) / Y) * p.y);
    return smax(outerlimit, scaleAdjusted - branchwidth, 0.2) * 0.5;
}

float insulate_tree(vec3 p, float t)
{
    float dp = p.z; // distance to plane
    float df = TLC123_tree2d(p.xy, 3); // distance to "surface"/outline given by 2d distance function
    // My original formula (circular cross-section)
    return sqrt(dp*dp+df*df)-t; // expand/3dify 2d SDF

    // Variants suggested by FabriceNeyret2
    //return min(max(abs(dp),df+t), sqrt(dp*dp+df*df))-t; // try this: FILLED (takes interior into accound)
    //return df-.2*(1.-abs(dp)); // nice paper-like shape
    //return df+.2*abs(dp); // even nicer, more subtle paper-like style
    //return max(df,dp) - t; // rectangle
    //return max(df,abs(dp)) - t; // flat rectangle

    // Variants suggested by Shane
    //return max(abs(df), abs(dp) - .15) - t; // "Hacky rectangle cross section"
    //return max((abs(df) + abs(dp))*.7071, max(abs(df), abs(dp))) - t; // "Hacky octagon cross section"
    //return length(vec2(df, dp)) - t; // Circle
}

float map(vec3 p)
{
    float d = mix(0.01, 0.05, 0.5 + 0.5 * cos(iTime));
    return
        min
        (
            insulate_tree(p, d),
            halfspace(p, -1.2)
        );
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h)
            +
            k.yyx * map(p + k.yyx*h)
            +
            k.yxy * map(p + k.yxy*h)
            +
            k.xxx * map(p + k.xxx*h)
        );
}

float trace(vec3 ro, vec3 rd)
{
    for(float t = 0.0; t < DIST_MAX;)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return DIST_MAX;
}

// https://iquilezles.org/articles/rmshadows
float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<EPSILON )
            return 0.0;
        t += h;
    }
    return 1.0;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

#define pi 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ndc = 2.0 * uv - 1.0;
    ndc.x *= float(iResolution.x) / float(iResolution.y);
    float mx = 2.0 * pi * float(iMouse.x) / float(iResolution.x);
    float my = pi / 2.0 + pi / 2.0 * float(iMouse.y) / float(iResolution.y);
    mx = (iMouse.x != 0.0) ? mx : 2.0 * pi * fract(iTime * 0.1);
    my = (iMouse.y != 0.0) ? my : pi * 3.0 / 4.0;;

    mat2 R = mat2(vec2(cos(mx), sin(mx)), vec2(-sin(mx), cos(mx)));
    vec3 ro = vec3(0.0, 0.0, -5.0 );//vec3(0.0, -10.0 * my, 0.0);
    //mat2 S = mat2(vec2(0.0, 1.0), vec2(-1.0, 0.0));
    mat2 S = mat2(vec2(cos(my), sin(my)), vec2(-sin(my), cos(my)));
    ro.yz=S*ro.yz;
    ro.xy = R * ro.xy;

    vec3 rd = normalize(vec3(0.5 * ndc.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact
    rd.yz=S*rd.yz;
    rd.xy = R * rd.xy;

    float dist = trace(ro, rd);
    vec3 dst = ro + rd * dist;
    vec3 n = normal(dst);

    vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    vec3 ambient = vec3(0.4);
    float brightness = max(dot(lightdir, n), 0.0);
    brightness *= shadow(ro+rd*dist,lightdir, 0.01, DIST_MAX); // XXX artifacts on cylinder XXX
    vec3 color = vec3(1.0);
    color *= (n * 0.5 + 0.5);
    color = (ambient * calcAO(dst, n) + brightness) * color;

    fragColor = mix(vec4(color, 1.0), vec4(0.0), step(DIST_MAX, dist));
    fragColor = sqrt(fragColor); // approximate gamma
}
