/*
This function is the most important one here
It outputs the distance between a point (position) and the nearest point in 
our shape (if there were multiple shapes, we would need the take the min). 
This function is used at every iteration of the raymarcher to get if the ray hits
the shape or not.
Since we need to also output the color of the hitten pixel we pass color buffer 
(inout keyword is similar to a pointer)

*/
float GetDistance(in vec3 position, inout vec3 color) {
        vec2 sphereUV; //This is the buffer where a point is mapped in the sphere
                       //So that we can texture it 
        vec3 inter = sphere0.center + sphere0.radius*normalize(position - sphere0.center);
        vec3 n = normalize(inter - sphere0.center); //Normal of a the point in SPHERE (don't need to use here GetNormal then, the shape)
        sphereUV.x = atan(n.x, - n.z) / (PI*2.0) + 0.5; //https://en.wikipedia.org/wiki/UV_mapping
        sphereUV.y = 0.5 + asin(n.y)/PI;

        color = texture(iChannel0, sphereUV * TEXTURE_REPETITION).xyz;

        //Getting the distance to the nearest point in the shape
        //Making factor equal to 0 will make the shape just a phere
        //Substracting varying value (depending here on the uv) will stylize the shape as it is done
        float factor = perlin(sphereUV * PERLIN_FREQUENCY, 0.5,3.0, 6.0,5.0).x * heightIntensity;
        float dist = length(sphere0.center - position) - (sphere0.radius - factor);

        return dist; 
    }


/*Function that differentiate a vector relatively to x,y,z
   (we make the difference with a small value of each coordinate )to
    get normal at that point. parameter point is the point where 
    we want to get the normal, and prec is the precision to the normal
    which basically is the macro already defined
*/
vec3 GetNormal(vec3 point, float prec) {
    float dis = GetDistance(point, none);
    vec2 epsilon = vec2(prec, 0.0);
    return normalize (dis -
                vec3(
                GetDistance(point - epsilon.xyy, none),
                GetDistance(point - epsilon.yxy,  none),
                GetDistance(point - epsilon.yyx, none)
                ));
}



vec3 RayMarcher(in camera camera, inout vec3 newPos) {
    //March from camera till hitting an object or passing depth
    float march = .05;
    vec3 position = camera.position + camera.lookAt*march;
    vec3 outColor = BG;
    float nearestObj = 0.0;
    int counter = 0;
    for (int i = 1; i < MAX_STEPS; i++) {
       nearestObj = GetDistance(position, outColor);
       march += nearestObj;
       position = camera.position+march*camera.lookAt;
    }

   if (nearestObj > DEPTH) {
    return BG; //If depth distance is passed we will render BG color in that pixel
   }
   //There is here only one iteration
   //Here is applied the classical Blinn-Phong model 
   // https://en.wikipedia.org/wiki/Blinn%E2%80%93Phong_reflection_model
    for (int i = 0; i < LIGHT_NUM; i++) {
            vec3 projection =  normalize(-position + lights[i].position);
            vec3 normal = GetNormal(position, NORMAL_PRECISION);
            vec3 reflection = reflect(normal, projection);
            vec3 blinn = normalize(projection - camera.lookAt);
            float specular  = pow(max(0.0,dot(blinn, normal)),SPECULAR_QUALITY);
            float diffuse = dot(projection, normal);
            vec3 result = (specular+diffuse)*lights[i].color + AMBIENT_LIGHT;
            outColor *= result;
    }

    newPos = position;
    return outColor;

 }




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //making screen coord centred in the middle and orthonormal
    screenCoord = (fragCoord - 0.5* iResolution.xy)/iResolution.y;
    vec2 mouse =  iMouse.xy/iResolution.xy;

    heightIntensity = 0.3*mouse.x;

    vec3 lookAt = normalize(vec3(screenCoord.xy,1.0));
    //lookAt = EulerRotation(lookAt,10.0*sin(iTime/2.0),0.0,0.0);  //Camera rotation testing
    vec3 origin = vec3(0.0,0.0,0.0);
    //BG = vec3(screenCoord.y, 0.0,screenCoord.y);
    //It's a bit of a hack; we take rand() and apply power to it to make it fade and just 
    //little white point will remain
    BG = vec3(pow(rand(screenCoord), STARS));
    camera cam = camera(origin, lookAt);
    //Our output
    vec3 col = RayMarcher(cam, origin);

    fragColor = vec4(col,1.0);
}

/*
The following shader implements the following to generate this beautiful shape:

 - One iteration of raytracing (no reflection)
 - Blinn-Phong reflection/lighting model
 - Perlin noise to make the geography of the shape
 - Perlin noise to generate the starry sky in the background
 - Implementation of camera rotation (won't affect the background however)

*/


struct sphere {
   vec3 center;
   vec3 color;
   float radius;
};

struct light {
    vec3 position;
    vec3 color;
};

struct camera {
    vec3 position;
    vec3 lookAt;
};




//User Parameters
//Sphere and light struct initialization you can set position, radius, color
sphere sphere0 = sphere(  vec3(0.0, 0.0,2.5), vec3(1.0,0.0, 0.1), 1.0);   
light lights[] = light[] ( light (vec3(0.0,0.0,0.0), vec3(1.0)  ) );

//---------Raytracing Parameters

//MAX_STEPS: Indicates how many march in Raymarcher function 
//(Note that in our particular scene we don't need that much, the more there is the lower the fps is)
#define MAX_STEPS 50
//MIN_DISTANCE: The distance between a point and an object below which we consider there is collision
#define MIN_DISTANCE 0.01
#define NORMAL_PRECISION 0.001
//DEPTH: How far you can see in the scene
#define DEPTH 10.0

//---------Noise Parameters
//PERLIN_OCTAVES: Sets the quality of the perlin noise, bigger this is, better is the perlin noise, however the fps decreases very quickly
#define PERLIN_OCTAVES 1
//PERLIN_FREQUENCY: Sets the frequency of noise in this particular scene
#define PERLIN_FREQUENCY 1.1

//---------Light Parameters
//Ambient Light color and specular reflection model quality
#define AMBIENT_LIGHT vec3(0.0,0.0,0.0)
#define SPECULAR_QUALITY 50.0


//---------Other
//TEXTURE_REPETITION: number of times the texture of iChannel0, mapped in the sphere, should repeat
#define TEXTURE_REPETITION 1.0
//STARS: Bigger this is, less stars there are out there
#define STARS 300.0
//If you add a light object to associated array, make sure to increase this
#define LIGHT_NUM 1
//---------------------------------------------------------------

#define PI 3.14159265359

float heightIntensity = 0.0;
vec3 none; //Buffer that will contain undesired output of GetDistance() function in the case of it's call from within GetNormal()
vec3 BG = vec3(0.0); //Background Color, it's written in mainImage()
vec2 screenCoord;  






//NOISE FUNCTIONS
//-----------------------------------------------------------------------------------

//Classic fractional random function and noise function, see TheBookOfShaders for more details
float rand(vec2 uv) {
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

float noise(vec2 uv, float freq) {
    uv *= freq;
    vec2 fractional = fract(uv);
    vec2 integer = floor(uv);
    fractional = fractional*fractional*(3.0-2.0*fractional);
    vec2 down = vec2( rand(integer), rand(integer + vec2(1.0, 0.0)) );
    vec2 up = vec2( rand(integer + vec2(0.0,1.0)), rand(integer + vec2(1.0, 1.0)) );
    float x = mix(down.x, down.y, fractional.x);
    float y = mix(up.x, up.y, fractional.x);
    return mix(x,y, fractional.y);
}

//Perlin uses noise function and superimpose multiple layers
//of it with different frequencies, which we name Octaves, at the end we normalize
vec3 perlin(vec2 uv, float amplitudeMul, float freqMul,float amplitude, float freq)  {
    vec3 col = vec3(noise(uv, 10.0)*10.0);
    float total = amplitude;
    for (int i = 0; i<PERLIN_OCTAVES; i++) {
        freq *= freqMul;
        amplitude *= amplitudeMul;
        col += vec3(noise(uv, freq)*amplitude);
        total += amplitude;
    }
    col/= total;
    return col;
}




/*
    This function rotates a vector, given angles in degree
    i'm putting it here in the case if camera is meant to rotate
    then, we have just to rotate the vector lookAt (see mainImage).
    Basically it uses rotation matrix from linear algebra
*/
vec3 EulerRotation(vec3 vector, float x, float y, float z) {
   x*= PI/180.0;
   y*= PI/180.0;
   z*= PI/180.0;

   vec3 rot = mat3(
            1,0,0,
            0,cos(x), -sin(x),
            0,sin(x), cos(x)
           )
           *
           mat3(
            cos(y), -sin(y), 0,
            sin(y), cos(y), 0,
            0,0,1
           )
           *
           mat3(
            cos(z), 0, sin(z),
            0, 1, 0,
            -sin(z), 0, cos(z)
           )
           *vector;

   return rot;
}

// Copyright Â© 2022 Wildric Auric


//Change macros value to generate the best scales output

//Scales shape parameters
float SHAPE_HORIZONTAL_RADIUS  =0.4;
float SHAPE_VERTICAL_RADIUS = 1.8;
float PATTERN_FREQUENCY = 6.0;


//Color Parameters
#define SCALE_COLOR vec3(1.0,1.0,1.0)
#define NOISE false
#define BLACK_BORDER true
#define COLOR_INTENSITY 1.0

#define RANDOMIZE_COLOR false
#define colIntensity vec3(1.0,1.0,1.0)



//Light Parameters
#define LIGHT false
#define LIGHT_COLOR vec3(1.0, 1.0, 1.0)
#define LIGHT_INTENSITY 1.0


//Animation parameters

#define ANIMATION true
#define ANIMATION_SPEED 0.5


//OTHER

#define LIGHT_Z_POS 0.5





//-----------------------------------------------------------

vec2 originalUV = vec2(0.0);
bool passed = false;
vec3 lightPosition = vec3(0.5,0.5,LIGHT_Z_POS);





vec3 CalculateNormal(vec2 coord,float len) {
    return normalize(
           vec3(
                coord.x*0.4,
                coord.y*0.4,
                1.0
                )
            );
}


vec3 MagicalColorRandomizer(vec2 uv) {
    vec3 r = perlin(uv, 0.5, 2.0, 100.0, 3.0)*colIntensity.r; //arbitrary values in perlin function to generate "galatical" colors
    vec3 g = perlin(uv+0.1, 0.5, 2.0, 100.0, 3.0)*colIntensity.g;
    vec3 b = perlin(uv + 0.5, 0.5, 4.0, 100.0, 1.0)*colIntensity.b;
    float fade = pow(length(perlin(uv-0.1, 0.6, 2.0, 10.,10.0)), 30.0)*4.0;
    return vec3(length(r),length(g), length(b))/sqrt(3.0);

}




vec3 Lit(vec2 position, vec3 normal) {
    vec3 dir = normalize(vec3(position.x,position.y, 0.0) - lightPosition);
    return -LIGHT_INTENSITY*dot(dir, normal) * LIGHT_COLOR;

}




vec3 ShapePattern(vec2 uv,vec2 center) {
    uv -= center;
    vec2 localCoord = (uv + vec2(SHAPE_HORIZONTAL_RADIUS*0.5, 0.0))
                       /vec2(SHAPE_HORIZONTAL_RADIUS, SHAPE_VERTICAL_RADIUS);
    float len = pow(uv.x/(SHAPE_HORIZONTAL_RADIUS), 2.0) +  pow(uv.y/(SHAPE_VERTICAL_RADIUS), 2.0);
    float offsetu = 0.0;
    if (ANIMATION) offsetu =  0.05*cos(iTime*ANIMATION_SPEED);
    if (  len<= 1.3 + offsetu && uv.y < 0.0) {
        //Point is inside the shape
        passed = true;
        localCoord = -localCoord;

        vec3 result =  SCALE_COLOR * vec3(smoothstep(0.0,0.15,localCoord.y/2.0));
        if (RANDOMIZE_COLOR) {
            result *= MagicalColorRandomizer(originalUV);
        };
        if ( NOISE ) result *= 2.0* noise(localCoord, 1.0);
        if (BLACK_BORDER) {
                result *= vec3(smoothstep(1.0, 0.0,len/3.0));
        }
        if (LIGHT) 
            result *= Lit(originalUV, CalculateNormal(localCoord,len));
        return result ; 


    }
    return vec3(0.0,0.0,.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
    PATTERN_FREQUENCY = 10.0 + 0.5*(sin((cos(iTime*ANIMATION_SPEED) - uv.x * uv.y)*2.0)+1.0);


    //PATTERN_FREQUENCY = 6.0 + sin(iTime);
    originalUV = uv;
    uv *= float(PATTERN_FREQUENCY);

	uv.x += 0.05*sin(uv.y*10.0+iTime)/10.0;

    vec2 mouse = iMouse.xy/iResolution.xy;
    lightPosition.xy = mouse.xy;

    vec3 col = vec3(0.0);


    float offset = 0.0;
    for (int y = 0; y < int(float(PATTERN_FREQUENCY)/(SHAPE_VERTICAL_RADIUS)*2.0)+4; y++) {
        for (int x = 0; x < int(float(PATTERN_FREQUENCY)/2.0/SHAPE_HORIZONTAL_RADIUS)+3; x++) {
               if (passed) break;
               vec2 center = vec2( (2.0*float(x) + offset) * float(SHAPE_HORIZONTAL_RADIUS),
                                    float(PATTERN_FREQUENCY)+1.0- float(y)*SHAPE_VERTICAL_RADIUS*0.5);
               col += ShapePattern(uv, center);
       }
       offset = 1.0 - offset;
    }

    // Output to screen
    fragColor = vec4(col + 0.15,1.0);
}
