void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float star_radius = 40.0;
    float glow_strength = 100.0;
    float noise_strength = 0.1;

    vec4 sphere = vec4(1.0,1.0,0.4,1.0);
    fragColor = vec4(0.0,0.0,0.0,1.0);


    // Distance to center of screen
    vec2 center = iResolution.xy/2.0;
    float dist = distance(center, fragCoord);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x -= iTime / 100.0;
    float noise = (length(texture(iChannel0, uv).rgb)/sqrt(3.0));

    // Draw sphere
    if (dist <= star_radius) {
        fragColor = sphere * (1.0-noise_strength) + noise * (noise_strength);
    }
    // Draw Glow
    else if (dist <= star_radius * 4.0) {
        float distance_fallof = min(1.0/((dist-star_radius)*(dist-star_radius)*1.0/glow_strength), 1.0);
        fragColor = (sphere * (1.0-noise_strength) + noise * (noise_strength)) * distance_fallof;
    }
}
