#define time iTime
#define resolution iResolution.xy

const float pi = 3.14159265358979323846264;
const float innerRadius = 0.25;
const float outerRadius = 0.3;
const float offset = 0.125;
const float fadeEdge = 0.008;
const float speed = 2.0;
const vec3 color = vec3(0.2, 0.6, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 aspect = vec2(resolution.x/resolution.y, 1.0);
  	vec2 uv = aspect*(fragCoord.xy / resolution);
	vec2 circleOrigin = aspect*vec2(0.5, 0.5);
	vec2 posRelCircle = uv-circleOrigin;

	float d = distance(circleOrigin, uv);
	if (innerRadius < d && d < outerRadius) {
		float angle = 1.0-mod(atan(posRelCircle.y, posRelCircle.x)/(2.0*pi) + speed*time / 10.0 , 1.0);
		float c = smoothstep(0.1,0.95,angle)*(1.0 - smoothstep(0.97,1.0,angle));
        c *= (1.0 - smoothstep(outerRadius-fadeEdge, outerRadius, d))*smoothstep(innerRadius, innerRadius+fadeEdge, d);
        fragColor = vec4(color*c, 1.0);
	} else {
        fragColor = vec4(0);
	}
}
