// Blackhole Golf 1: https://shadertoy.com/view/fsKSzc
// Blackhole Golf 2: https://shadertoy.com/view/7dGSDy

#define N(p)  texture( iChannel0, A*p ).r
#define A    ( mix( x*dot(p,x), p, cos(t) ) - sin(t) *cross(p,x) )

void mainImage(out vec4 O, vec2 C)
{
    vec3 x = vec3(.22,.97,0),
         r = iResolution,
         d = vec3( C+C -r.xy, -r ) / r.x,
         p = d; r -= r;

    float R,a=.05, i=a, t = iTime/4.+5.;
    for( p.z+=4.;  i++<2e2 ; p += d/40. )
        a *= min( R=dot(p,p), .2 ) / .2,
        r += (2.+d) * N(1.) * N(.3)
             * a / max(A*A*8e2,R).y / R,
        d -= p/5e2/R/R;

    p = d, 
    O.gbr = max(r + (2.+d) / (R*R+.02/a-9.) + a* A * N(.2),
                N( 5./length(d) ) /.2 -4. );
}




