#define PI 3.141592653589793

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    uv *= 3.;
    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    float l = abs(length(gv));
    float theta = atan(gv.y, gv.x);

    l += sin(theta * 8.) * l * .05 * cos(theta + iTime * 20.);
    l += sin(theta * 3.) * l * .16 * cos(theta + iTime * 9.* id.x * .1);

    float r = .3;
    float thickness = .02;
    float circle = smoothstep(r, r + thickness / 2., l) + smoothstep(r, r - thickness / 2., l);

    vec3 col = vec3(circle);

    fragColor = vec4(col,1.0);
}
