float line(float a, float d, float blur){
 float l = smoothstep(length(a), length(a)-d, blur);
 return l;
 }
float circle(vec2 uv, vec2 p, float r, float blur){
     float d = length(uv-p);
     float c = smoothstep(r, r-blur, d);

     return c;

}float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1*step2;

    }

float rectangle(vec2 uv, float up, float down, float left, float right, float blur){
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, up, down, blur);

    return band1*band2;

    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )


{
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float x = uv.x;
    float y = uv.y;
    float d = ((x-.5)*(x+.5));

    float face = circle(uv, vec2(0., 0.), .3, .01);   // makes the face 
    vec3 color = mix(YELLOW, RED, uv.y+.35); // makes the color gradient of the face
    vec3 Face = color*vec3(face); // colors the face

    float brow1 = rectangle(vec2(x+.05, (y-.4)-(d*2.)+x*.1), -.01,  .01, -.05, .05, .005); // makes the brow
    float brow2 = rectangle(vec2(x-.1, (y-.27)-(d*1.5)-x*.1), -.01,  .01, -.05, .05, .005); // makes the second brow
    float brow = brow1+brow2;//adds both brows
    vec3 Brow = vec3(brow); // makes it a vec3 value

    float eye1 = circle(uv, vec2(-0.05, -0.1), .02, .005); // makes the first eye
    float eye2 = circle(uv, vec2(0.1, -0.09), .02, .005); // makes the second eye
    float eye = eye1+eye2; // adds both eyes
    vec3 Eye = vec3(eye); // makes it a vec3 value

    float mouth = rectangle(vec2(x-.03, y+.2), -.01, .01, -.05, .05, .005); // makes the mouth
    vec3 Mouth = vec3(mouth); // makes it a vec3 value

    float dis1 = -.14;// didn't want to write the same value over and over again so made a float
    float dis2 = -.12;

    float tear1 = circle(vec2((x-.274)-d*.5, y+.008), vec2(-0.05, -.15), .04, .005); // makes the first tear
    float t1 = rectangle(vec2(x-.105, (y)-dis2), -.019, .019, -.019+y-dis2, .019-y+dis2, .005);// makes the tear pointy
    tear1 += t1; // adds the pointy bit
    float tear2 = circle(vec2((x+.122)+d*.5, y+.023), vec2(-0.05, -.15), .04, .005); // makes the first tear
    float t2 = rectangle(vec2(x+.05, (y)-dis1), -.019, .019, -.019+y-dis1, .019-y+dis1, .005);// makes the tear pointy
    tear2 += t2; // adds the pointy bit
    float tear = tear1+tear2; // adds both tears
    vec3 Tear = vec3(tear)*(color-BLUE); // colors it


    vec3 Emoji = Face-Brow-Eye-Mouth-Tear; //final product

    vec3 col = mix(vec3(1.2), Emoji, vec3(face)); //makes the bg white



    fragColor = vec4(col,1.0);
}
