/***************************************
  *              Settings              *
  ***************************************/

float res = 15.; // how many sqrs along the x axes
float seed = 429496.7295;
bool debug = false;
float timeSpeed = 0.1;
int balls = 20;
float ballSize = 5.;

/***************************************
  *          Define Variables          *
  ***************************************/

float pixelSize = 0.0;
float time = 0.;


/***************************************
  *               Random               *
  ***************************************/

float random (vec2 st) {
    return
        fract(
                sin( 
                    dot(
                         st.xy
                        ,vec2(12.9898,78.233)
                    )
                )
            *seed
            +seed
        );
}

/***************************************
  *               Lerp                 *
  ***************************************/
float lerp(float f1, float f2, float t)
{
	return (f1 + t * (f2-f1));
}

vec2 lerp(vec2 v1, vec2 v2, float t){
	return (v1 + t * (v2-v1));
}
vec2 initPos(int id){
    return vec2(
        random(vec2(id)),
        random(vec2(id + 313))
    ) * res * 2. - res;
}

/***************************************
  *                Mod                 *
  ***************************************/

int modInt(int i, int j){
    return int(mod(float(i),float(j)));
}

/***************************************
  *             Main Code              *
  ***************************************/
vec2 pos(int id){
    //float time = iTime / 10.;
    int first = id + int(time) * 3;
    int second = first + 1;
    int third = first + 2;
    int forth = first + 3;

    vec2 pos1 = initPos(first);
    vec2 pos2 = initPos(second);
    vec2 pos3 = initPos(third);
    vec2 pos4 = initPos(forth);

    float t =  fract(time);

    vec2 lerp1 = lerp(pos1, pos2, t);
    vec2 lerp2 = lerp(pos3, pos4, t);

    return lerp(lerp1, lerp2, t);
}

vec3 randomCol(int id){
    return vec3(
        random(vec2(id)),
        random(vec2(id + 513)),
        random(vec2(id + 541313))
    );
}

float cirkle(vec2 uv,vec2 pos, float size)
{
    float dist = length(uv - pos);
    float m = smoothstep(size, .01, dist);

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime * timeSpeed;
    vec2 uv = ((fragCoord - .5 * iResolution.xy) / (iResolution.x/res));
    pixelSize = res / iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);
    for(int i = 0; i < balls; i++){
        int id = i * 500;
        vec3 addCol = 
             randomCol(id)
            *vec3(
                cirkle(
                     uv
                    ,pos(id)
                    ,ballSize
                )
            );
        col += addCol;

        if(debug){
            id += int(time) * 3;
            float id1 = .1/length(uv - initPos(id + 0));
            float id2 = .1/length(uv - initPos(id + 1));
            float id3 = .1/length(uv - initPos(id + 2));
            float id4 = .1/length(uv - initPos(id + 3));

            col += vec3(id1, 0, 0);
            col += vec3(0, id2, 0);
            col += vec3(0, 0, id3);
            col += vec3(id4);
        }
    }
    if( debug && (
        fract(uv.x) <= pixelSize || fract(uv.x) >= 1.-pixelSize || 
        fract(uv.y) <= pixelSize || fract(uv.y) >= 1.-pixelSize)
    ) col.r = 1.;

    // Output to screen
    fragColor = vec4(col,1.0);
}
