//color palette from shader by iq entitled "Palettes"
void mainImage( out vec4 O, in vec2 C )
{
    float blur = .065;
    float len = 24.;
    vec2 uv = (2.*C-iResolution.xy)/iResolution.y;
    vec3 c = vec3(.1, .2, .7) * .55; 
    for (float i = .0; i <= len; i++) {
        float t = iTime - .1*i;
        float ss_i = smoothstep(.0, blur,
                         length(vec2(((uv.x - sin(t)*.7) - sin(t*8.)*.25),
                                      (uv.y - cos(t)*.7) - cos(t*8.)*.25)) - .15);
        vec3 c_i = vec3(.5) + (vec3(.5) * cos(6.28318*((vec3(i / len)+iTime*.25)+vec3(.0,.33,.67))));
        c = mix(c_i, c, ss_i);
    }
    O = vec4(c, 1.);
}
