//// Edited via https://www.shadertoy.com/view/4dcyD2#
//// Created by WillKirkby in 2018-03-04
////
/// Added heartbeat sound



float circle(vec2 p, float r){
	return r-length(p);
}

float scene(vec2 p){
    vec2 p1=p;
    if(abs(p.x)<.85&&abs(p.y)<.35)
    	p1=mod(p+.05,.1)-.05;

    //p-=mod(p+.05,.1)-.05;
    float r = texture(iChannel0, vec2(length(p)*.15,0)).r;
    return circle(p1,.06*r*r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const float cinematicAspect = 2.35;
	float currAspect = iResolution.x/iResolution.y;

    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x *= currAspect;

    float d = scene(uv);

    fragColor = 1.-clamp(vec4(d*iResolution.y*.5),0.,1.);
    fragColor.rgb = mix(
    	vec3(11,231,184)/255.,
        vec3(30,57,77)/255.,
        fragColor.rgb
    );

    if (abs(uv.y) > .5*(currAspect/cinematicAspect))
    {
		fragColor *= 0.;
	}
    else
    {
		fragColor = fragColor * (length(uv)*-.5+1.) + texture(iChannel3,fragCoord/256.)*.004;
		//                      ^ vignette           ^ noise to hide banding
	}
}
