// Public domain.

#define SHIFT .5

vec3 rect(vec3 color, vec2 uv, vec2 bl, vec2 tr)
{
    float res = 1.;

    // Bottom left.
    bl = step(bl, uv);  // if arg2 > arg1 then 1 else 0
    res = bl.x * bl.y;  // similar to logic AND

    // Top right.
    tr = step(SHIFT - tr, SHIFT - uv);
    res *= tr.x * tr.y;

    return res * color;
}

vec3 square(vec3 color, vec2 uv, vec2 c, float sz)
{
    return rect(color, uv, c - sz, c + sz);
}

vec3 drawA(vec3 res, vec3 color, vec2 uv, float sz)
{
    // A.
    // A: top.
    res += square(color, uv, vec2( 0.,  .2), sz);

    // A: left side.
    res += square(color, uv, vec2(-.1,  .1), sz);
    res += square(color, uv, vec2(-.1,  .0), sz);
    res += square(color, uv, vec2(-.1, -.1), sz);
    res += square(color, uv, vec2(-.1, -.2), sz);

    // A: right side.
    res += square(color, uv, vec2( .1,  .1), sz);
    res += square(color, uv, vec2( .1,  .0), sz);
    res += square(color, uv, vec2( .1, -.1), sz);
    res += square(color, uv, vec2( .1, -.2), sz);

    // A: center.
    res += square(color, uv, vec2( 0., 0.), sz);

    return res;
}

vec3 drawB(vec3 res, vec3 color, vec2 uv, float sz)
{
    // B: top.
    res += square(color, uv, vec2( 0.,  .2), sz);
    res += square(color, uv, vec2(-.1,  .2), sz);

    // B: left side.
    res += square(color, uv, vec2(-.1,  .1), sz);
    res += square(color, uv, vec2(-.1,  .0), sz);
    res += square(color, uv, vec2(-.1, -.1), sz);
    res += square(color, uv, vec2(-.1, -.2), sz);

    // B: right side.
    res += square(color, uv, vec2( .1,  .1), sz);
    res += square(color, uv, vec2( .1, -.1), sz);

    // B: center.
    res += square(color, uv, vec2( 0., 0.), sz);

    // B: bottom.
    res += square(color, uv, vec2( 0., -.2), sz);

    return res;
}

vec3 drawC(vec3 res, vec3 color, vec2 uv, float sz)
{
    // C: top.
    res += square(color, uv, vec2( 0.,  .2), sz);

    // C: left side.
    res += square(color, uv, vec2(-.1,  .1), sz);
    res += square(color, uv, vec2(-.1,  .0), sz);
    res += square(color, uv, vec2(-.1, -.1), sz);

    // C: right side.
    res += square(color, uv, vec2( .1,  .1), sz);
    res += square(color, uv, vec2( .1, -.1), sz);

    // C: bottom.
    res += square(color, uv, vec2( 0., -.2), sz);

    return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1).
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Remap coordinates to make 0.0 be at the center.
    uv -= SHIFT;

    // Account for the aspect ratio.
    uv.x *= iResolution.x / iResolution.y;

    // Paint colors.
    vec3 red    = vec3(.667, .133, .141);
    vec3 blue   = vec3(0.,   .369, .608);
    vec3 yellow = vec3(1.,   .812, .337);
    vec3 black  = vec3(0.);

    // Output color.
    vec3 color = black;

    // Size.
    float sz = 0.05;

    float speed = 2.;
    float t = iTime * speed;
    float f = sin(t);

    if (f >= .0 && f < .4 || f <= .0 && f > -.4) {
        // B.
        color += drawB(color, blue, uv, sz);
    } else if (f > 0.) {
        // A.
        color += drawA(color, red, uv, sz);    
    } else if (f < 0.) {
        // C.
        color += drawC(color, yellow, uv, sz);
    }

    // Output to screen.
    fragColor = vec4(color, 1.);
}
