//try click around




// Parameter Input
float circleRange = 0.3;

float sdfCircle(vec2 uv, float r ,float smoothRange , vec2 offset) {

    float screenRatioX = iResolution.x/iResolution.y;
    float x = uv.x - offset.x * screenRatioX;
    float y = uv.y - offset.y;

    float d = length(vec2(x, y));
    float c = 1. -smoothstep(r-smoothRange,r+smoothRange,d);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // <0,1>
  uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

  vec2 mouse = vec2(iMouse)/vec2(iResolution);   


    if (iMouse.z < 1.0)
    {
        mouse = vec2(0.5);
    }



  //Color
  float gradCir = sdfCircle(uv, circleRange-0.025 , 0.1 , mouse);
  float cirY = 1. - smoothstep(mouse.y - circleRange,mouse.y + circleRange,uv.y);
  float lerpF = sdfCircle(uv, circleRange , 0.01 , mouse); 

  vec3 colBG = mix(vec3(1.,0.,1.),vec3(0.,1.,1.),uv.y);
  vec3 colCir1 = mix(vec3(0.,1.,1.),vec3(1.,0.,1.),gradCir);  
  vec3 colCir2 = mix(vec3(0.,1.,1.),vec3(1.,0.,1.), cirY);
  vec3 colCir3 = colCir1 * colCir2 + 0.25;

  vec3 col = mix(colBG,colCir3,lerpF);

  // Output to screen
  fragColor = vec4(col,1.0);
}
