float distLine(vec3 camPos, vec3 rayDir, vec3 point)
{
    return length(cross(point - camPos, rayDir)) / length(rayDir);
}

float drawPoint(vec3 camPos, vec3 rayDir, vec3 point)
{
    float dist = distLine(camPos, rayDir, point);

    dist = smoothstep(.06, .05, dist);

    return dist;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 camPos = vec3(5.*sin(time), 5., 5.*cos(time));
    vec3 lookAt = vec3(.5);
    float zoom = 1.;
    vec3 forward = normalize(lookAt - camPos);
    vec3 right = cross(vec3(0., 1., 0.), forward);
    vec3 up = cross(forward, right);
    vec3 center = camPos + forward*zoom;
    vec3 intersectionPoint = center + uv.x*right + uv.y*up;

    vec3 rayDir = intersectionPoint - camPos;


    //vec3 point = vec3(sin(time), 0., 1.+cos(time));		// A rotating point.

    float dist = 0.;

    // Count in binary up to 7 to create a cube.
    dist += drawPoint(camPos, rayDir, vec3(0., 0., 0.));
    dist += drawPoint(camPos, rayDir, vec3(0., 0., 1.));
    dist += drawPoint(camPos, rayDir, vec3(0., 1., 0.));
    dist += drawPoint(camPos, rayDir, vec3(0., 1., 1.));
    dist += drawPoint(camPos, rayDir, vec3(1., 0., 0.));
    dist += drawPoint(camPos, rayDir, vec3(1., 0., 1.));
    dist += drawPoint(camPos, rayDir, vec3(1., 1., 0.));
    dist += drawPoint(camPos, rayDir, vec3(1., 1., 1.));

    // Output to screen
    fragColor = vec4(dist);
}
