vec4 number1 (vec2 uv)
{
    float FREQUENCY = 5.0;
    float AMPLITUDE = .3;
    vec2 circlePos = vec2(0.5,0.25);
    float radius = 0.15;
    vec4 FG = vec4(0,0,1,1.0);
    vec4 BG = vec4(0,1,0,1.0);
    vec4 returnValue;
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;
    circlePos.y = circlePos.y * aspectRatio + ((sin(iTime * FREQUENCY)+1.0)/2.0) * AMPLITUDE;
    float dist = distance(circlePos,uv);
    float isOutOfCircle = smoothstep(radius-.002,radius+.002,dist);
    vec4 col = mix(FG,BG,isOutOfCircle);
    returnValue = vec4(col);
    return returnValue;
}

vec4 number2 (vec2 uv)
{
    const float FREQUENCY = 2.5;
    const float AMPLITUDE = .5;
    vec2 circlePos = vec2(0.5,0.5);
    float radius = 0.15;
    vec4 FG = vec4(0,0,1,1.0) + ((sin(iTime * FREQUENCY)+1.0)/2.0) * AMPLITUDE;
    vec4 BG = vec4(0,1,0,1.0);
    vec4 returnValue;
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;
    circlePos.y *= aspectRatio;
    float dist = distance(circlePos,uv);
    float isOutOfCircle = smoothstep(radius-.002,radius+.002,dist);
    vec4 col = mix(FG,BG,isOutOfCircle);
    returnValue = vec4(col);
    return returnValue;
}

vec4 number3(vec2 uv)
{
    uv.y *= iResolution.y/iResolution.x;
    vec2 location1 = vec2(.5,.3);
    vec2 location2 = vec2(.45,.25);
    vec2 location3 = vec2(.55,.35);
    float radius = .1;
    vec3 bigColor = vec3(0);
    vec3 color1 = vec3(1,0,0);
    vec3 color2 = vec3(0,1,0);
    vec3 color3 = vec3(0,0,2);

    vec4 layer1 = vec4(bigColor,1.0);
    vec4 layer2 = vec4(color1,0.0);
    vec4 layer3 = vec4(color2,0.0);
    vec4 layer4 = vec4(color3,0.0);

    if(length(location1-uv) < radius)
        layer2.a=1.0;
    if(length(location2-uv) < radius)
        layer3.a=.475;
    if(length(location3-uv) < radius)
        layer4.a=.475;

    vec4 composite = mix(layer1,layer2,layer2.a);
    composite = mix(composite,layer3,layer3.a);
    composite = mix(composite,layer4,layer4.a);
    return composite;
}

vec4 number4(vec2 uv)
{
    const float FREQUENCY =2.0;
    const float AMPLITUDE = 1.0;
    vec4 image1 = texture(iChannel0,uv);
    vec4 image2 = texture(iChannel1,uv);
    float change = ((sin(iTime * FREQUENCY)+1.0)/2.0) * AMPLITUDE;
    vec4 screen = mix(image1,image2,change);
    return screen;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = number1(uv);
    //fragColor = number2(uv);
    //fragColor = number3(uv);
    //fragColor = number4(uv);
}
