#define Pi acos(-1.0)

//  https://www.desmos.com/calculator/icuindny2w
//  Not actually sin(x), it's -cos(x).
float DerpSine(float x) {
    float y = round(abs(abs(mod(x-Pi,2.0*Pi))/Pi-1.0)) * (-1.0*pow((abs(mod(x,2.0*Pi))-Pi)/(Pi/2.0),2.0)+1.0) + round(abs(mod(abs(x),2.0*Pi)/Pi-1.0)) * (pow((abs(mod(x-Pi,2.0*Pi))-Pi)/(Pi/2.0),2.0)-1.0);
    return y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float color = DerpSine(fragCoord.x*0.042+iTime) * 0.5 + 0.5;

    fragColor = vec4(vec3(color),1.0);
}
