#define wScale 18.0
#define nScale 0.5
#define wSpeed 1.7
#define nSpeed 1.1
#define verticalMul 0.5
#define waterFallSpeed 0.1
#define phase (iTime + 512.0)

//fork from Dave Hoskins
//https://www.shadertoy.com/view/4djSRW
vec4 hash43(vec3 p)
{
	vec4 p4 = fract(vec4(p.xyzx) * vec4(1031. , .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19 - phase *0.01 * waterFallSpeed);
	return -1.0 + 2.0 * fract(vec4(
        (p4.x + p4.y)*p4.z, (p4.x + p4.z)*p4.y,
        (p4.y + p4.z)*p4.w, (p4.z + p4.w)*p4.x)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = 0.01;
    vec2 uv = fragCoord/iResolution.xy;
    uv *= scale; uv.y *= verticalMul;

    vec4 warpNoise = hash43(vec3(uv *wScale, phase *0.0008 * wSpeed));
    warpNoise.y += abs(warpNoise.x *1.2 -1.0);
    //vec4 warpNoise = vec4(0);
    vec4 hashNoise = hash43(vec3(uv + vec2(warpNoise.y, 0) *0.0000001 *nScale, -phase *0.0003 *nSpeed));
    vec3 col = vec3(hashNoise.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}
