float L(vec2 p, vec2 a,vec2 b) {               // draw line
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) );                       
}
vec2 hexgrid(inout vec2 p){
    const vec2 sz = vec2(1.,1.73),hs=sz*.5;

    vec2 pa = mod(p,sz)-hs;
    vec2 pb = mod(p-hs,sz)-hs;
    vec2 pc = dot(pa,pa) < dot(pb,pb) ? pa:pb;
    vec2 n = (p-pc+hs)/sz;
    p = pc;
    return round(n*2.)*.5;


  }
#define S(v) smoothstep(4.*15./iResolution.y,0.,v)  

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

      uv *=10.;
  vec2 uuv = uv*.5;
  vec2 n = hexgrid(uv);
  uv*=4.;
  vec3 col = vec3(0.);
  float gy = dot(sin(n),cos(n.xy));
	vec2 I = floor(uv),J, M=vec2(sin(iTime+gy+n.x+n.y*.2)*.2,cos(iTime+gy+n.x*.2+n.y)*.5);
  for(int k=0;k<25;k++){
      J = I + vec2(k%5-2,k/5-2);
      float d=  length(J-M);
      d> 1. && d < 2. ? col +=S(L(uv,J,M))*vec3(1.):col;

    }

    fragColor = vec4(col,1.0);
}
