#define PI 3.14                          //define的写法



float func(float x)                    //随时间变动的sin函数
{
    float t=cos(iTime);
    return sin(t*x);
}

float plotfunc(vec2 uv)
{
    return smoothstep(func(uv.x),(func(uv.x)+0.01),uv.y);

}




vec2 fixuv(vec2 c)
{
    return (3.0*(2.0*c-iResolution.xy)/iResolution.y);
}




void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fixuv(fragCoord);

    vec3 color;
    color.g=plotfunc(uv)*0.8;
    color.b=plotfunc(uv)+0.8;
    fragColor=vec4(color,0.0);    

}
