const int RAY_STEPS = 256;
const float FOVY = 3.141569 * 0.25;

// The higher the value, the smaller the penumbra
const float SHADOW_HARDNESS = 10.0;
// Adjust these to alter where the subsurface glow shines through and how brightly
const float FIVETAP_K = 2.0;
const float AO_DIST = 0.085;
// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.0;

#define IDBackWall 1
#define IDLeftWall 2
#define IDRightWall 3
#define IDCeilingWall 4
#define IDFloorWall 5
#define IDLongCube 6
#define IDShortCube 7
#define IDSphere 8

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

void sceneMap3D(vec3 pos, out float t, out int obj)
{
    // Initialize to back wall sdf
    t = plane(pos, vec4(0.0, 0.0, -1.0, 5.0));
    obj = IDBackWall;

    float t2;
    // Check left wall
    if((t2 = plane(pos, vec4(1.0, 0.0, 0.0, 5.0))) < t)
    {
        t = t2;
        obj = IDLeftWall;
    }
    // Check right wall
    if((t2 = plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))) < t)
    {
        t = t2;
        obj = IDRightWall;
    }
    // Check top ceiling wall
    if((t2 = plane(pos, vec4(0.0, -1.0, 0.0, 7.5))) < t)
    {
        t = t2;
        obj = IDCeilingWall;
    }
    // Check floor wall
    if((t2 = plane(pos, vec4(0.0, 1.0, 0.0, 2.5))) < t)
    {
        t = t2;
        obj = IDFloorWall;
    }
    // Check for long cube
    if((t2 = box(rotateY(pos + vec3(-2, 1, 1), -27.5 * 3.14159 / 180.0), vec3(1.5, 4, 1.5))) < t)
    {
        t = t2;
        obj = IDLongCube;
    }
    // Check of short cube
    if((t2 = box(rotateY(pos + vec3(2, 1, 1.75), 17.5 * 3.14159 / 180.0), vec3(1.5, 1.5, 1.5))) < t)
    {
        t = t2;
        obj = IDShortCube;
    }
    // Check for sphere light source
    if ((t2 = sphere(pos, 1., vec3(3.0 * cos(iTime), 1.0 + 2.0 * sin(iTime), 1))) < t) {
        t = t2;
        obj = IDSphere;
    }
}

float sceneMap3D(vec3 pos)
{
    // find the smallest t value for marching
    float t = plane(pos, vec4(0.0, 0.0, -1.0, 5.0));

    t = min(t, plane(pos, vec4(1.0, 0.0, 0.0, 5.0)));
    t = min(t, plane(pos, vec4(-1.0, 0.0, 0.0, 5.0)));
    t = min(t, plane(pos, vec4(0.0, -1.0, 0.0, 7.5)));
    t = min(t, plane(pos, vec4(0.0, 1.0, 0.0, 2.5)));
    t = min(t, box(rotateY(pos + vec3(-2, 1, 1), -27.5 * 3.14159 / 180.0), vec3(1.5, 4, 1.5)));
    t = min(t, box(rotateY(pos + vec3(2, 1, 1.75), 17.5 * 3.14159 / 180.0), vec3(1.5, 1.5, 1.5)));

    return t;
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    // If there is no object in 256 steps
    t = -1.0;
    hitObj = -1;
}

// Follow the code in Visual Effects PPT
float subsurfaceTerm(vec3 lightDir, vec3 normal, vec3 viewVec, float thin) {
    vec3 scatterDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatterDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    float totalLight = attenuation * (lightReachingEye + AMBIENT) * thin;
    return totalLight;
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view, float thin)
{
    // Use the simple lamber shading for this scene
    float lambert = abs(dot(-lightVec, n));
    float bssrdf = subsurfaceTerm(-lightVec, n, view, thin);
    vec3 lightCol = vec3(1.0, 0.88, 0.7);
    switch(hitObj){
        case IDLongCube:
        return (bssrdf + lambert) * lightCol * vec3(0.5, 1., 0.);
        break;
        case IDShortCube:
        return (bssrdf + lambert) * lightCol * vec3(0., 0.5, 1.);
        break;
        case IDBackWall:
        return lightCol * vec3(1., 1., 1.) * lambert;
        break;
        case IDLeftWall:
        // red left wall
        return lightCol * vec3(0.25, 1., 0.5) * lambert;
        break;
        case IDRightWall:
        // Green right wall
        return lightCol * vec3(1., 0.5, 0.25) * lambert;
        break;
        case IDCeilingWall:
        // Black ceiling
        return lightCol * vec3(1., 1., 1.) * lambert;
        break;
        case IDFloorWall:
        return lightCol * vec3(1., 1., 1.) * lambert;
        break;
        case IDSphere:
        return vec3(1.0, 0.88, 0.7);
        case -1:
        return vec3(0., 0., 0.);
        break;
    }
    return vec3(0., 0., 0.);
}

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    // Set the light as a point light
    vec3 lightPos = vec3(3.0 * cos(iTime), 1.0 + 2.0 * sin(iTime), 1);

    vec3 lightDir = normalize(isect - lightPos);
    float thin = fiveTapAO(isect, -nor, FIVETAP_K);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thin);


    return Intersection(t, surfaceColor, isect, hitObj);
}


vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc)
{
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0.0, 1.0, 0.0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // [-1, 1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(0, 3, -20);

    vec3 ref = vec3(0, 0, 100);

    vec3 rayDir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(rayDir, eye);

    // Output to screen
    fragColor = vec4(isect.color, 1.0);
}
