// neural activity? :)

mat2 rot(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}


float fx(vec2 uv,float fadetime)
{
    float tt = iTime;
	float light = 0.05;
    float rip = 0.5+sin(length(uv)*5.0+tt)*0.5;
    rip = 1.0+rip*0.4;
    uv*=rip*0.5;
    uv *= rot(tt*0.055);

    float vv2 = smoothstep(0.0,0.45,fadetime);
    float vv = mix(0.01,0.15,fadetime);


    tt*=0.5;
	for (float x = 1.5; x < 24.0; x += 2.0)
	{
		uv *= rot(x*4.0+length(uv)*0.126*sin(tt*0.05));
		vec2 f = vec2(cos(cos(tt*0.6+x + uv.x * x) - uv.y * dot(vec2(x + uv.y), vec2(sin(x), cos(x)))));
		light += (vv / distance(uv, f)) - (0.01 * distance(vec2((cos(tt*0.3 + uv.y))), vec2(uv)));
		light-=x*0.001 + 0.001;
	}


    light = clamp(light,0.0,1.0);
    return light*vv2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float fadetime = fract(iTime*0.1);

    fadetime = 0.5+sin(iTime*0.5)*0.5;

    float fade = fx(uv,fadetime);

    uv = fragCoord/iResolution.xy;
    vec3 c1 = texture(iChannel0, uv).xyz;
    vec3 c2 = texture(iChannel1, uv).xyz;
    if (iMouse.z>0.5)
    {
        c1 = vec3(0.0);
        c2 = vec3(1.0);
    }
    c1 = mix(c1,c2,fade);
    fragColor = vec4(c1,1.0);
}
