void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.yx/iResolution.yx - 0.5) * 4.0;
    vec2 mouse = (iMouse.yx/iResolution.yx - 0.5) * 4.0;

    uv -= mouse + 0.0;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float x = 0.0;
    float y = 0.0;
    int iter = 0;
    float pi = 3.1415927;

    while (x * x + y * y <= 4.0 * (1.0 + sin(iTime) * 0.0) && iter < 1000)
    {
        float xt = x * x - y * y * (1.0 + sin(8.0 * sin(iTime * (sin(iTime / 4.0) * 0.5 + 1.0)) * uv.x) * sin(iTime * 0.9) * 0.5) + uv.x;
        y = 2.0 * x * y * (1.0 + cos(iTime) * 2.5) + uv.y;
        x = xt;
        iter += 1;
    }

    float val = float(iter) * cos(iTime * 0.5) + 0.2 * cos(iTime * 16.0);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*vec3(sin(val / 5.0), sin(val / 3.0 + pi * 2.0 / 3.0), sin(sin(val / 10.0)));

    // Output to screen
    fragColor = vec4(col / (float(iter) * 0.01 + 1.0),1.0);
}
