float colorBand1(float value, float colorSteps) {
    return floor(value * colorSteps) / colorSteps;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float fix = iResolution.x/iResolution.y;

    uv.x *= iResolution.x / iResolution.y;

     vec2 toCenter = vec2(.5*fix,.5)-uv;
    float radius = length(((toCenter)));

    vec3 final;

    float c = 20.0;
    float a = colorBand1(radius, c);


    float fff = mix(0.,1.0,(sin(a*5.0-iTime)));

    final  = mix( vec3(0.9, 0.3, 0.9),
                    vec3(0.0, 0.0,0.), fff);


    // Output to screen
    fragColor = vec4(final,1.0);
}
