void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 bloom = texture(iChannel1,uv);
    vec4 bg = texture(iChannel0, uv);
    fragColor = bg*0.6+bloom*vec4(1.0, 1.0, 0.0, 1.0)*pow(sin(iTime), 2.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Output to screen
    vec4 color = texture(iChannel0,uv);
    float l = (0.2126*color.r + 0.7152*color.g + 0.0722*color.b);
    fragColor = smoothstep(0.7,0.71, l)* color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 delta = vec3(1.0/iResolution.xy, 0.0);

    vec4 color = vec4(0.0);
    float k = 1.0;
    float total = 0.0;
    for(int i = 0; i < 32; i ++){
        color += k*texture(iChannel0, uv+float(i)*delta.xz);
        color += k*texture(iChannel0, uv-float(i)*delta.xz);
        total += 2.0 * k;
        k = sqrt(k/2.0);
    }

    fragColor = color/total;
}

