//change this constant to get different patterns
#define c1 vec4(2.0,2.5,1.4,0)


vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 4.0;
    float offset = .18;
    float scale2 = 1.05;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        for(int i=0;i<9;i++)
        {

            uv = triangle_wave(uv+offset,scale)+triangle_wave(uv.yx,scale);
            uv.x /= -1.0;
            uv = triangle_wave(uv+c1.w+col.xy,scale);
            scale /= scale2+col.x;
            offset *= scale2;
            //uv.y /= -1.0;
            uv = uv.yx;
            //uv = uv.yx;

        }
     col[c] = fract((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}
