float random(vec2 p) {
    return fract(sin(p.x*100. + p.y*6574.)*5647.);

    //float a = dot(p, vec2(12.9898,78.233));
    //return fract(sin(a*43758.5453123));
}
float noise(vec2 p){
    vec2 id = floor(p);
    vec2 localUv = fract(p);
    localUv = smoothstep(0.0, 1.0, localUv);


    // Mix four corner
    float bl = random(id);
    float br = random(id + vec2(1.0, 0.0));
    float bottom = mix(bl, br, localUv.x);

    float tl = random(id + vec2(0.0, 1.0));
    float tr = random(id + vec2(1.0, 1.0));
    float top = mix(tl, tr, localUv.x);

    return mix(bottom, top, localUv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(noise(uv*30.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
