float n = 10000.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y*4.-vec2(2.*iResolution.x/iResolution.y,2.);
    vec2 z = vec2(0);
    float d = 0.;
    for(float i = 0.; i<n; i++)
    {
        d++;
        z = vec2(z.x*z.x-z.y*z.y+uv.x,2.*z.x*z.y+uv.y);
        if(length(z)>2.)
            break;
    }
    if(d == n)d = 0.;
    fragColor = vec4(vec3(mod(d/30.,1.)),1.0);
}
