#define n 3.0

float random (float i){
 return fract(sin(i*43.0)*4790.234);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;
    vec2 tc = uv;
    float aspect = iResolution.x / iResolution.y;


    vec4 c = vec4(0.0);
    for(float i =1.0; i<n; i++){
        float r = random(i);
        float r2 = random(i+n*1.0);
        float r3 = random(i+n*2.0);
        float r4 = random(i+n*3.0);
        float r5 = random(i+n*4.0);

        //vec2 p = uv - vec2(aspect * 0.5, 0.5) - vec2(sin(iTime * 0.5 + r * 30.0), cos(float(i*10) + iTime * 0.25 + r * 40.0)) * r;
        vec2 p = uv - vec2(aspect * 0.5, 0.5);

        float pct = i / n;

        p.x -= sin(pct * 6.28 + iTime * 0.2) * 0.4;
        p.y -= cos(pct * 6.28 + iTime * 0.2) * 0.4;

        vec4 col = vec4(r2, r3, r4, r5 / dot(p, p) * 2.0);
        col.rgb *= col.a * col.a;
        //col.rgb = pow(col.rgb, vec3(1.6));

        c += col;
    }

    c.rgb = normalize(c.rgb);

    fragColor = vec4(c);

}
