float circle(vec2 uv, vec2 pos, float rad, float blur)
{
    float dist = length(uv - pos);
    float col = smoothstep(rad, rad - blur, dist);

    return col;
}

float band(float pos, float start, float end, float blur)
{
    float step_1 = smoothstep(start - blur, start + blur, pos);
    float step_2 = smoothstep(end + blur, end - blur, pos);

    return step_1 * step_2;
}

float rect(vec2 uv, float left, float right, float btm, float top, float blur)
{
    float band_1 = band(uv.x, left, right, blur);
    float band_2 = band(uv.y, btm, top, blur);

    return band_1 * band_2;
}

float smiley(vec2 uv, vec2 pos, float size)
{
    uv -= pos;		// translating coord system.
    uv /= size;		// scaling coord system.

    float mask = circle(uv, vec2(0), .4, .01);

    mask -= circle(uv, vec2(0.13, 0.2), .1, .01);
    mask -= circle(uv, vec2(-0.13, 0.2), .1, .01);


    float mouth = circle(uv, vec2(0.0, 0.0), .31, .02);
    mouth -= circle(uv, vec2(0.0, 0.1), .3, .02);
    //col = vec3(mouth);

    mask -= mouth;

    return mask;
}

float simRemap(float a, float b, float num)
{
    return (num-a) / (b-a);
}

float remap(float a, float b, float c, float d, float num)
{
    return simRemap(a, b, num) * (d-c) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    float time = iTime;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0);

    float x = uv.x;


    float mask = 0.;
    //float m = -(x-.5)*(x+.5);
    //m = -(m*m*4.);

    float m = 0.1*sin(time+x*8.0);

    float y = uv.y+m;

    float blur = remap(-.5, .5, .01, .25, x);
    blur = pow(blur*3., 3.);

    mask = rect(vec2(x, y), -.5, .5, -.1, .1, blur);

    col = vec3(1.0, 1.0, 1.0)*mask;


    // Output to screen
    fragColor = vec4(col, 1.0);
}
