#define lofi(i,j) (floor((i)/(j))*(j))

const float SQRT3 = sqrt(3.0);
const float HALF_SQRT3 = 0.5 * sqrt(3.0);
const float REC_SQRT3 = 1.0 / SQRT3;
const float PI = acos(-1.0);
const float TAU = 2.0 * PI;

mat2 r2d(float t){
    float c = cos(t);
    float s = sin(t);
    return mat2(c, -s, s, c);
}

vec2 fart(vec2 p, vec2 v) {
    float d = dot(p, v);
    float df = mod(d, 2.0);
    float di = d - df;
    return p - (di + (df > 1.0 ? 2.0 * df - 2.0 : 0.0)) * v / dot(v, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = uv * 2.0 - 1.0;
    p.x *= iResolution.x / iResolution.y;

    p = fart(p, vec2(2.0, 0.0));
    p = fart(p, vec2(-1.0, SQRT3));
    p = fart(p, vec2(-1.0, -SQRT3));

    p = r2d(iTime) * p;

    p.x *= iResolution.y / iResolution.x;
    p = 0.5 + 0.5 * p;

    fragColor = vec4(p, 0.0, 1.0);
    fragColor = texture(iChannel0, p);
}

