const float PI = 3.1415926;
const float TAU = 2.*PI;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv.y -= .35;
    uv *= min(sin(iTime / 20.)* 3., 7.);

    vec3 col = vec3(1., 0.41, 0.70);

    float c = sin((9.25 * uv.y) + (8.0 * iTime)) * cos((9.25 * uv.x));

    float shape =  smoothstep(1.9 - clamp(distance(c + uv.y, 0.5) * 0.9, 0.0, 1.0), 1.0, 0.9);
    if (shape > 0.5) {
       col = vec3(1., 1., .7);
    } else {
        col = vec3(134. / 255.  + uv.y, 226. / 255., 213. / 255.);
        col *= 1. - shape;
    }

	fragColor = vec4(col,1.0);
}
