//Load custom textures https://www.shadertoy.com/view/lsGGDd

//https://stackoverflow.com/a/4275343
float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

//https://stackoverflow.com/a/596243
float luminance(vec4 col) {
    return (0.2126*col.x + 0.7152*col.y + 0.0722*col.z);
}

float blackWhite(vec4 col) {
    return (col.x + col.y + col.z) / 3.0;
}

vec4 darkNoise(vec4 col, vec2 uv) {
    return col -= rand(uv);
}

vec4 lightNoise(vec4 col, vec2 uv) {
    return col += rand(uv);
}

//https://danielilett.com/2019-05-08-tut1-3-smo-blur/
vec4 blur(vec2 uv, int size, sampler2D channel) {
    int upper = ((size - 1) / 2);
    int lower = -upper;
    vec4 sum = vec4(0.0, 0.0, 0.0, 0.0);
    for (int x = lower; x <= upper; ++x) {
        for (int y = lower; y <= upper; ++y) {
            vec2 offset = vec2(x, y)/iResolution.xy;
            sum += texture(channel, uv+offset);
        }
    }
    sum /= float(size*size);
    return sum;
}

