float scale = 2.0;

float noise(vec2 uv, int waves){
    float n = 0.0;
    for (int i = 0; i < waves; i++){
        n += sin((uv.x+float(i))*pow(2.0,float(i))) + sin((uv.y+float(i))*pow(2.0,float(i)));
    }
    return n/float(waves+1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 100.0*scale;
    uv -= iMouse.xy;

    float n = noise(uv/100.0, 2)*15.0-2.0;
    float time = iTime*5.0;

    float wave = n;

    vec3 col = vec3(0,0.5,0.8);

    float whitecap = sin(uv.x/8.0 + time/6.0)/2.0+0.5 + sin(uv.y/8.0 + time/6.0)/2.0+0.5;
    if (whitecap < 0.9){
        col += vec3(0.1);
    }

    if (abs(sin(uv.x*100.0 - time) + sin(uv.y*100.0 - time)) + n > 0.1){
        col = vec3(1);

        if (n >= 0.0 + clamp(sin(uv.x/3.0)+sin(uv.y/3.0)/2.0,0.0,1.0)){
            col = vec3(0,n/15.0+0.3,0);

            if (n <= 1.0*(sin(uv.x)+sin(uv.y)/2.0 + 0.9)){
                col = vec3(n/15.0+0.8,n/15.0+0.8,0);
            }
        }
    }

    fragColor = vec4(col,1.0);
}
