vec3 marem(float a, vec2 uv, vec3 mare, vec3 col){
return mix(mare,col,a) ;
}

vec3 duck(vec2 uv, float scale, vec2 pos){
uv *= scale;
float k = length(uv-vec2(0.45*sin(uv.x-pos.x),0.3*cos(uv.x-pos.x))-pos);
float kg = length(uv-vec2(-0.147,0.47)-pos);
float kd = length(uv-vec2(-0.278,0.439)-pos);
float kd1 = length(uv-vec2(-0.32,0.482)-pos);
float ko = length(uv-vec2(-0.18,0.517)-pos);
float kob = length(uv-vec2(-0.176,0.524)-pos);
vec3 zol = vec3(1.,1.,0.0);
vec3 cze = vec3(1.,0.,0.0);
  vec3 colk;
  k = 1.-step(0.15,k);
  colk = mix(colk,zol,k);
  kg = 1.-step(0.1,kg);
  colk = mix(colk,zol,kg);
  kd = 1.-step(0.05,kd);
  colk = mix(colk,cze,kd);
  kd1 = 1.-step(0.07,kd1);
  colk = mix(colk,vec3(0.),kd1);
  ko = 1.-step(0.017,ko);
  colk = mix(colk,vec3(0.,0.,0.1),ko);
  kob = 1.-step(0.0037,kob);
  colk = mix(colk,vec3(1.),kob);
  return colk;
}


