renderpass.code:
float nsin(in float n) {
    return sin(n) * .5 + .5;
}

float rnd21(vec2 p) {
    p = fract(p * vec2(233.34, 851.73));
    p += dot (p, p + 23.45) ;
    return fract(p.x * p.y);
}


float circle(in vec2 p, in vec2 center, in float diameter) {
    float AA = 1. / iResolution.y;
    return smoothstep(AA + diameter, diameter - AA, distance(p, center));
}

float line_segment(in vec2 p, in vec2 a, in vec2 b, in float thickness) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    float AA = 1. / iResolution.y;
    return smoothstep(AA + thickness, thickness - AA, length(pa - h * ba));
}

float triangle(in vec2 p, in vec2 a, in vec2 b, in vec2 c, in float thickness) {
    float col = 0.;
    col += line_segment(p, a, b, thickness);
    col += line_segment(p, b, c, thickness);
    col += line_segment(p, c, a, thickness);
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec2 mouse = (iMouse.xy - .5 * iResolution.xy) / iResolution.y;

    vec2 a = vec2(-0.5 + sin(iTime) * 0.1, -0.3 + cos(iTime) * 0.1);
    vec2 b = mouse;

    float col = 0.;

    // two dots, sin motion and mouse, linked by line
    col += circle(uv, a, 0.02);
    col += circle(uv, b, 0.02);
    col += line_segment(uv, a, b, 0.005);

    // triangle
    col += triangle(uv, vec2(0., 0.), vec2(0.2, 0.2), vec2(0.3, 0.), 0.005);

    col += circle(uv, vec2(0.3, 0.3), sin(iTime * 2.) * 0.01 + .1);

    fragColor = vec4(vec3(col), 1);
}
