// This is one of my first shaders.
// Special thanks to FabriceNeyret2's <https://www.shadertoy.com/view/llySRh>
// Special thanks to PrzemyslawZaworski's <https://www.shadertoy.com/view/4sBfRd>
#define CHARACTERS_WIDE 16
#define CHARACTERS_WIDE_F float(CHARACTERS_WIDE)
#define CHARACTER_WIDTH 1./CHARACTERS_WIDE_F
#define CHARACTERS_TALL 16
#define CHARACTERS_TALL_F float(CHARACTERS_TALL)
#define CHARACTER_HEIGHT 1./CHARACTERS_TALL_F
void draw_character(inout vec4 color, in vec2 uv, in vec2 pos, in uint index) {
    uv -= pos;
    if (uv.x < 0. || uv.y < 0. || uv.x >= CHARACTER_WIDTH || uv.y >= CHARACTER_HEIGHT) return;
    uv.x += mod(float(index), CHARACTERS_WIDE_F)*CHARACTER_WIDTH;
    uv.y += (CHARACTERS_TALL_F-floor(float(index)/CHARACTERS_TALL_F)-1.)*CHARACTER_HEIGHT;
    color.rgb = texture(iChannel0, uv).rrr;
}
#define C(index) {\
    draw_character(color, uv, pos, index);\
    pos.x += CHARACTER_WIDTH;\
}
#define STRETCH 0
#define FIT 1
#define FILL 2
#define MODE FILL
void mainImage(out vec4 color, in vec2 uv) {
    #if MODE == STRETCH
        uv /= iResolution.xy;
    #elif MODE == FIT
        uv /= iResolution.y;
        uv.x += iResolution.y/iResolution.x-1.; // Doesn't work as expected.
        uv = mod(uv, 1.);
    #elif MODE == FILL
        uv /= iResolution.x;
        uv.y -= iResolution.x/iResolution.y-2.;
    #endif
    color = texture(iChannel1, uv);
    //color = vec4(uv.x, uv.y, 0., 1.);
    vec2 pos = vec2(0., .5-CHARACTER_HEIGHT/2.);
    C(0x48U);C(0x65U);C(0x6cU);C(0x6cU);C(0x6fU);C(0x2cU);C(0x20U);C(0x77U);C(0x6fU);C(0x72U);C(0x6cU);C(0x64U);C(0x21U);
}
