float drawCircle(vec2 dot, vec2 center, float radius, float blur){
    float len = length(dot - center);

    return smoothstep(radius, radius - blur, len);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;


    float mask = drawCircle(uv,vec2(0,0),.4,0.02);
    vec3 col = vec3(1.,1.,1.) * mask;

    if(mask > 0.){
        col = vec3(1.,1.,.0) * mask;
    }

    float mouse = drawCircle(uv, vec2(0,0),0.3, 0.02);

    if(mask == mouse){
        col= vec3(1.,1.,1.);
    }


    float face = drawCircle(uv, vec2(0,.08),0.3, 0.03);

    if(face == mask && face > 0.){
        col= vec3(1.,1.,.0);
    }

    float right_eye = drawCircle(uv, vec2(0.15,0.1),0.08, 0.01);
    float left_eye = drawCircle(uv, vec2(-0.15,0.1),0.08, 0.01);

    if((right_eye == mask && right_eye > 0.) || (left_eye == mask&& left_eye > 0.)){
        col= vec3(0.6824, 0.1647, 0.1647);
    }

    float left_eye_ball = drawCircle(uv, vec2(-0.15,0.05),0.03, 0.01);
    float right_eye_ball = drawCircle(uv, vec2(0.15,0.05),0.03, 0.01);

    if((left_eye_ball == left_eye && left_eye_ball >0.) || (right_eye_ball == right_eye && right_eye_ball > 0.)){
        col= vec3(0.5,0.5,0.5);
    }

    mask -= mouse;
    mask -= face;


    mask -= right_eye;

    mask -= left_eye;

    // col = vec3(1.0,1.0,.0) * mask;

    // Output to screen
    fragColor = vec4(col,1.0);
}
